import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EmployeeForm from './pages/EmployeeForm';
import EmployeeTable from './pages/EmployeeTable';
import { Routes, Route, useNavigate } from 'react-router-dom';
import LoginSignup from './pages/LoginSignup';
import Dashboard from './pages/Dashboard';
import './App.css';
import Header from './pages/Header';

function App() {
  const [employees, setEmployees] = useState([]);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/employees', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
        // Optionally, navigate to login if token is invalid
        if (error.response && error.response.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchEmployees();
  }, [token, navigate]);

  const handleSave = () => {
    setEditingEmployee(null);
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/employees', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
    navigate('/employee-form');
  };

  const handleDelete = (id) => {
    setEmployees((prev) => prev.filter((emp) => emp.f_Id !== id));
  };

  return (
    <>
      <Header />
      <Routes>
        <Route path="/login" element={<LoginSignup />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/employee-list" element={<EmployeeTable employees={employees} onEdit={handleEdit} onDelete={handleDelete} />} />
        <Route
          path="/employee-form"
          element={
            editingEmployee ? (
              <EmployeeForm employee={editingEmployee} onSave={handleSave} onCancel={() => setEditingEmployee(null)} />
            ) : (
              <EmployeeForm onSave={handleSave} onCancel={() => setEditingEmployee(null)} />
            )
          }
        />
        <Route path="/" exact element={<LoginSignup />} />
      </Routes>
    </>
  );
}

export default App;
