import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EmployeeForm from './pages/EmployeeForm';
import EmployeeTable from './pages/EmployeeTable';
import { Routes, Route, } from 'react-router-dom';
import LoginSignup from './pages/LoginSignup';
import Dashboard from './pages/Dashboard';
import './App.css'
import Header from './pages/Header';

function App() {

  const [employees, setEmployees] = useState([]);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const token = localStorage.getItem('token');
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        // Retrieve the token from localStorage
        const response = await axios.get('http://localhost:5000/api/employees', {
          headers: {
            Authorization: `Bearer ${token}`, // Attach the token to the request
          },
        });
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const handleSave = () => {
    setEditingEmployee(null);
    // Re-fetch employees to get the latest list
    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem('token'); // Retrieve the token
        const response = await axios.get('http://localhost:5000/api/employees', {
          headers: {
            Authorization: `Bearer ${token}`, // Attach the token
          },
        });
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  };

  const handleEdit = (employee) => {
    setEditingEmployee(employee);
  };

  const handleDelete = (id) => {
    setEmployees((prev) => prev.filter((emp) => emp.f_Id !== id));
  };

  return (
    <>
      <Header></Header>
      {
        token && (
          <div>
            <h1>Create Employee </h1>
            {editingEmployee ? (
              <EmployeeForm employee={editingEmployee} onSave={handleSave} onCancel={() => setEditingEmployee(null)} />
            ) : (
              <EmployeeForm onSave={handleSave} onCancel={() => setEditingEmployee(null)} />
            )}
          </div>
        )
      }
      <Routes>
        <Route path="/login" element={<LoginSignup />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path='/employee-list' element={<EmployeeTable employees={employees} onEdit={handleEdit} onDelete={handleDelete} />} />
        <Route path="/" exact element={<LoginSignup />} />
      </Routes>


    </>

  );
}

export default App;
