import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EmployeeForm = ({ employee, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    f_Name: '',
    f_Email: '',
    f_Mobile: '',
    f_Designation: '',
    f_gender: '',
    f_Course: [],
    f_Image: null
  });

  useEffect(() => {
    if (employee) {
      setFormData(employee);
    }
  }, [employee]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData((prev) => ({ ...prev, f_Image: e.target.files[0] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    for (const key in formData) {
      formDataToSend.append(key, formData[key]);
    }

    try {
      if (employee) {
        await axios.put(`http://localhost:5000/api/employees/${employee.f_Id}`, formDataToSend,);
      } else {
        await axios.post('http://localhost:5000/api/employees/create', formDataToSend);
      }
      onSave();
    } catch (error) {
      console.error('Error saving employee:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="f_Name"
        value={formData.f_Name}
        onChange={handleChange}
        placeholder="Name"
        required
      />
      <input
        type="email"
        name="f_Email"
        value={formData.f_Email}
        onChange={handleChange}
        placeholder="Email"
        required
      />
      <input
        type="tel"
        name="f_Mobile"
        value={formData.f_Mobile}
        onChange={handleChange}
        placeholder="Mobile"
        required
      />
      <input
        type="text"
        name="f_Designation"
        value={formData.f_Designation}
        onChange={handleChange}
        placeholder="Designation"
        required
      />
      <input
        type="text"
        name="f_gender"
        value={formData.f_gender}
        onChange={handleChange}
        placeholder="Gender"
        required
      />
      <input
        type="text"
        name="f_Course"
        value={formData.f_Course}
        onChange={(e) => setFormData((prev) => ({ ...prev, f_Course: e.target.value.split(',') }))}
        placeholder="Courses (comma-separated)"
        required
      />
      <input
        type="file"
        name="f_Image"
        onChange={handleFileChange}
      />
      <button type="submit">Save</button>
      <button type="button" onClick={onCancel}>Cancel</button>
    </form>
  );
};

export default EmployeeForm;
